    private void read(string path, List<int> beforeL){
        string[] lines = File.ReadAllLines(path);
        foreach (string line in lines)
        {
            string[] numbers = line.Split(',');
            foreach (string number in numbers)
            {
                int value;
                if (int.TryParse(number, out value))
                {
                    beforeL.Add(value);
                }
            }
        }
    }
