            if(answerT[3].text == OdpGPytanU[question[numberQuestion]]){
                good++;
                buttons[3].GetComponent<Image>().color = Color.green;
            }else{
                UPytaniaA.Add(question[numberQuestion]);
                buttons[3].GetComponent<Image>().color = Color.red;
                for(int i =0; i<4; i++){
                    if(answerT[i].text == OdpGPytanU[question[numberQuestion]]){
                        buttons[i].GetComponent<Image>().color = Color.green;
                    }
                }
            }
        }
        After();
        stopwatch.Stop();
    }
    private void After(){
        for(int i =0; i<4; i++){
            buttons[i].interactable = !buttons[i].interactable;
        }
        buttons[4].interactable = !buttons[4].interactable;
    }
    public void Reset(){
        PanelCategories.SetActive(true);
        PanelTest.SetActive(false);
        PanelEnd.SetActive(false);
        question.Clear();
        numberQuestion = -1;
        q = 0;
        good = 0;
        for(int i =0; i<4; i++){
            categories[i] = false;
        }
    }
}